CREATE DATABASE IF NOT EXISTS kingoludo;
USE kingoludo;
CREATE TABLE IF NOT EXISTS jeux(
   Nom           VARCHAR(70) NOT NULL PRIMARY KEY
  ,Année         INTEGER  NOT NULL
  ,Joueurs       VARCHAR(6) NOT NULL
  ,Duree         VARCHAR(9) NOT NULL
  ,Ages          VARCHAR(8)
  ,Categories    VARCHAR(39)
  ,Éditeurs      VARCHAR(197)
  ,Auteurs       VARCHAR(95)
  ,Illustrateurs VARCHAR(121)
);
